R=0.082054;disp('Programa para encontrar el volumen molar de los gases ideales');disp('Elija de cual gas desea obtener su volumen molar:');disp('1-Dioxido de carbono ');disp('2-Oxigeno');disp('#- gases ideales')disp('')opc=input('Ingrese su eleccion:'); p=input('Ingrese su presion (atm):  '); T=input('Ingrese su temperatura (k):  ');switch (opc)  case 1    a=3.59;    b=0.04267;    fv=inline('(p+(a/v**2))*(v-b)-R*T');  case 2    a=1.360;    b=0.03183;     fv=inline('(p+(a/v**2))*(v-b)-R*T');  otherwise    fv=inline('(R*T)/p');    fv endswitch  disp('');  disp('Metodo de Newton-Raphson');  v1=input('Ingresa punto de volumen de sugerencia:');  tol = input('Ingrese la tolerancia: ');  %Antes de pedir los limites graficar la funcion  c=0; n=1; %inicializar variables c-- propuesta, punto medio n-- iteraciones, contador  maxep=abs( fv(R,T,a,b,p,v1)); %error  fprintf('\t n \t v  \t       f(v) \t      maxep \n');  while(maxep>tol);%Cuando el error sea menor a la tolerancia, se obtiene la raiz        g=tol/10;      d=(fv(R,T,a,b,p,v1+g)-fv(R,T,a,b,p,v1-g))/(2*g);      if(d==0)          fprintf('Volumen molar de : %f L/mol\n', fv(R,T,p));          maxep=tol;      else         v1=v1-(fv(R,T,a,b,p,v1)/d);         maxep=abs(fv(R,T,a,b,p,v1));         fprintf("\t %i \t %f \t %f \t %f\n",n,v1,fv(R,T,a,b,p,v1),maxep);         n=n+1;      endif  endwhile  if(d!=0)    fprintf('Con la tolerancia de %f:\n\t\n', tol);     fprintf('Volumen molar de : %f L/mol', v1);  endif    %DATOS PARA ENTRAR: % CARBONO   % T       300                 500         700         % P   1     V= 24.5126      40.9821     57.4179  % P   10    V= 2.3525       4.0578      5.7242  % P   100   V= 0.2462       0.3663      0.5575    %OXIGENO  % T         300               500         700         % P   1     V= 24.5928      40.0259     57.4460  % P   10    V= 2.4384       4.1016      5.7521  % P   100   V= 0.2264       0.4116      0.5842    